def solution(stdin: str) -> str:
    W, H = map(int, stdin.split()[0].split())
    grid = [[char for char in row] for row in stdin[len(W * "#" + "\n") + 1:].split("\n")]

    def in_bounds(x, y):
        return 0 <= x < W and 0 <= y < H

    def is_player_position(x, y):
        return grid[y][x] == "P"

    def is_gold(x, y):
        return grid[y][x] == "G"

    def is_trap(x, y):
        return grid[y][x] == "T"

    def is_wall(x, y):
        return grid[y][x] == "#"

    def is_free_space(x, y):
        return grid[y][x].isalpha()

    def get_neighbors(x, y):
        return [(x + dx, y + dy) for dx in (-1, 0, 1) for dy in (-1, 0, 1) if in_bounds(x + dx, y + dy) and is_free_space(x + dx, y + dy)]

    def is_near_trap(x, y, traps):
        for dx, dy in get_neighbors(x, y):
            if (dx, dy) in traps:
                return True
        return False

    gold_count = 0
    player_position = (0, 0)
    traps = set()
    visited = set()

    while True:
        if player_position in visited:
            return str(gold_count)
        visited.add(player_position)

        for event, value in [("N", 1), ("S", -1), ("E", 0), ("W", 0)]:
            x, y = player_position
            next_x, next_y = x + value, y + value

            if (next_x, next_y) in traps:
                return str(gold_count)

            if is_free_space(next_x, next_y) or is_gold(next_x, next_y):
                player_position = (next_x, next_y)
                break

        if is_player_position(player_position[0], player_position[1]):
            gold_count += 1
            gold_position = (player_position[0], player_position[1])

        if is_gold(player_position[0], player_position[1]):
            gold_count += 1

        for dx, dy in get_neighbors(player_position[0], player_position[1]):
            if (dx, dy) in traps:
                traps.remove((dx, dy))

        if is_near_trap(player_position[0], player_position[1], traps):
            return str(gold_count)

        if is_wall(player_position[0], player_position[1]):
            return str(gold_count)

