        s = a + b + c
        return (a / s, b / s, c / s)

    def are_angles_congruent(a: float, b: float, c: float) -> bool:
        return a >= b >= c

    def is_similar_shape(a: float, b: float, c: float) -> bool:
        side_ratio = a / (a + b + c)
        return are_angles_congruent(a, b, c) and abs(side_ratio - 1) < 1e-4

