    if area > a:
        area = a

    x_min, y_min = min(p[0] for p in polygon), min(p[1] for p in polygon)
    x_max, y_max = max(p[0] for p in polygon), max(p[1] for p in polygon)

    if x_min < 0 and y_min < 0:
        x_min = 0
        y_min = 0

    x_delta, y_delta = x_max - x_min, y_max - y_min
    x_step, y_step = 1 if x_delta > 0 else -1, 1 if y_delta > 0 else -1

    while True:
        new_polygon = []
        for i, p in enumerate(polygon):
            new_p = (p[0] + x_step * x_delta, p[1] + y_step * y_delta)
            if is_similar_shape(triangle_area(p[0], p[1], new_p[0]), triangle_area(p[1], p[0], new_p[1]), triangle_area(new_p[0], new_p[1], new_p[0])):
