    # Calculate votes for each candidate
    candidate_votes = {}
    for name, count in votes.items():
        if count >= len(votes) / 2:
            candidate_votes[name] = candidate_votes.get(name, 0) + count

    # Determine winner(s)
    winner = None
    max_votes = 0
    for name, count in candidate_votes.items():
        if count > max_votes:
            max_votes = count
            winner = name

    # Output result
    if max_votes == len(votes) / 2:
        return winner
    else:
        return "Runoff!"

