        languages = set()
        for character, (name, _, _) in enumerate(characters):
            if any(language1 in additional_languages[character] for additional_languages in characters):
                languages.add(language1)
            if any(language2 in additional_languages[character] for additional_languages in characters):
                languages.add(language2)
        return languages

    def get_possible_pairs(characters: list[tuple[str, str, list[str]]]) -> set[tuple[str, str]]:
        pairs = set()
        for i, (name1, language1, additional_languages1) in enumerate(characters):
            for j, (name2, language2, additional_languages2) in enumerate(characters):
                if i!= j and any(language1 in additional_languages2 for additional_languages1 in characters):
                    pairs.add((name1, name2))
        return pairs

    def get_conversers(characters: list[tuple[str, str, list[str]]]) -> set[str]:
        conversers = set()
        for i, (name, language, additional_languages) in enumerate(characters):
            if any(language in additional_languages for additional_languages in characters):
                conversers.add(name)
        return conversers

    def count_characters(characters: list[tuple[str, str, list[str]]]) -> int:
        count = 0
        for _, _, additional_languages in characters:
            count += len(additional_languages)
        return count

    def count_conversers(characters: list[tuple[str, str, list[str]]]) -> int:
        count = 0
        for _, _, additional_languages in characters:
            count += len(additional_languages)
        return count

    def find_min_set(characters: list[tuple[str, str, list[str]]]) -> int:
        min_count = count_characters(characters)
        min_set = set()
        for i, (name, language, additional_languages) in enumerate(characters):
            if any(language in additional_languages for additional_languages in characters):
                min_set.add(name)
                min_count = min(min_count, count_characters(characters))
        return min_count

    possible_pairs = get_possible_pairs(characters)
    conversers = get_conversers(characters)

    while possible_pairs:
        min_count = find_min_set(characters)
        min_set = set()
        for name1, name2 in possible_pairs:
            if name1 in min_set and name2 in min_set:
                continue
            if name1 in conversers and name2 in conversers:
                continue
            if count_characters(characters) == min_count:
                min_set.add(name1)
                min_set.add(name2)
                break
        possible_pairs -= min_set
        conversers |= min_set

    return str(len(conversers))

