    cols = len(board[0])
    if 'K' not in board[1] or board[1].count('#') > 1:
        return -1
    def is_valid(r, c):
        return 1 <= r <= rows and 1 <= c <= cols

    def knight_moves(r, c):
        moves = [(r + 2, c + 1), (r + 2, c - 1), (r - 2, c + 1), (r - 2, c - 1),
                 (r + 1, c + 2), (r + 1, c - 2), (r - 1, c + 2), (r - 1, c - 2)]
        return [move for move in moves if is_valid(*move)]

    def knight_path(r, c, steps, visited=None):
        if not is_valid(r, c):
            return 0, steps
        if (r, c) == (1, 1):
            return 0, steps
        if visited is None:
            visited = set()
        if (r, c) in visited:
            return 0, steps
        visited.add((r, c))
        for move in knight_moves(r, c):
            steps += 1
            new_steps, new_steps_count = knight_path(move[0], move[1], steps, visited)
            if new_steps_count < steps:
                steps = new_steps_count
        return steps, steps

    steps = 0
    visited = set()
    _, steps_count = knight_path(1, 1, 0, visited)
    if steps_count < steps:
        return -1
    return str(steps)

