    key_phrase = [c for c in key_phrase.upper() if c.isalpha()]
    for i, c in enumerate(key_phrase):
        encryption_key[i] = encryption_key[i] + c

    # Encrypt the plaintext
    encrypted = ""
    for i in range(len(plaintext) - 1):
        if plaintext[i] == plaintext[i + 1]:
            encrypted += "X"
            continue

        # Map the plaintext digraph to the encryption key table
        if encryption_key[i] == plaintext[i]:
            encrypted += encryption_key[i] + encryption_key[i + 1]
        elif encryption_key[i] == plaintext[i + 1]:
            encrypted += encryption_key[i + 1] + encryption_key[i]
        else:
            encryption_key_row = encryption_key[i] + encryption_key[i + 1]
            encryption_key_col = encryption_key[i] + encryption_key[i + 2]
            encryption_key_corner = encryption_key[i] + encryption_key[i + 3]
            if encryption_key_row in encryption_key_col:
                encrypted += encryption_key_col[encryption_key_row.index(encryption_key_row)]
            elif encryption_key_col in encryption_key_row:
                encrypted += encryption_key_row[encryption_key_col.index(encryption_key_col)]
            elif encryption_key_corner in encryption_key_row and encryption_key_corner in encryption_key_col:
                encrypted += encryption_key_row[encryption_key_corner.index(encryption_key_corner)]

    # Remove the last 'x' added in the last iteration
    if encrypted[-1] == 'X':
        encrypted = encrypted[:-1]

    return encrypted.upper()

