    encrypted = encrypted.upper()
    key = key.upper()

    decrypted = ""
    key_index = 0

    for i, char in enumerate(encrypted):
        if i % 2 == 0:
            decrypted += chr((ord(char) - ord('A') + key_index) % 26 + ord('A'))
