def solution(stdin: str) -> str:
    def gcd(a: int, b: int) -> int:
        return gcd(b, a % b) if b!= 0 else b

    def lcm(a: int, b: int) -> int:
        return a * b // gcd(a, b)

    def is_multiple(a: int, b: int) -> bool:
        return a % b == 0

    def is_valid(a: int, b: int, c: int, d: int) -> bool:
        return all(is_multiple(a, x) and is_multiple(b, x) and is_multiple(c, x) and is_multiple(d, x) for x in (1, 2, 3, 4))

    def enclosed_area(a: int, b: int, c: int, d: int) -> int:
        return a * b + c * d - c * c - b * b

    def enclosing_rectangle(a: int, b: int, c: int, d: int) -> int:
        if a + b > c + d:
            a, b = c, d
            c, d = a + b - c - d, a + b + c + d

        return a + b

    def largest_rectangle(a: int, b: int, c: int, d: int) -> int:
        if is_valid(a, b, c, d):
            return max(enclosed_area(a, b, c, d), enclosing_rectangle(a, b, c, d))
        else:
            return 0

    def enclose_rectangle(a: int, b: int, c: int, d: int) -> int:
        if a + b > c + d:
            a, b = c, d
            c, d = a + b - c - d, a + b + c + d

        return enclosing_rectangle(a, b, c, d)

    a, b, c, d = map(int, stdin.split())

    return str(max(largest_rectangle(a, b, c, d), enclose_rectangle(a, b, c, d)))

def solution(stdin: str) -> str:
