def solution(stdin: str) -> str:
    n = int(stdin.split()[0])
    edges = [tuple(map(int, stdin.split()[1:])) for _ in range(n - 1)]

    graph = {i: [] for i in range(n)}
    for a, b in edges:
        graph[a - 1].append(b - 1)
        graph[b - 1].append(a - 1)

    def max_edges(a, b, c):
        max_a, max_b, max_c = -1, -1, -1

        for x in [a, b, c]:
            for path in graph[x]:
                if c in path:
                    max_a = max(max_a, len(path) - path.index(c))
                if b in path:
                    max_b = max(max_b, len(path) - path.index(b))
                if a in path:
                    max_c = max(max_c, len(path) - path.index(a))

        return max(max_a, max_b, max_c)

    def find_path(a, b, c):
        def dfs(x):
            nonlocal res, max_a, max_b, max_c
            if x == c:
                nonlocal count
                count += 1
                if count == 3:
                    res = max(res, max_edges(a, b, c))
            for i in graph[x]:
                if i!= b and i!= c and i!= a:
                    dfs(i)

        dfs(a)
        return res

    a, b, c = 0, 1, 2
    res, count = 0, 0
    while True:
        a, b, c = b, c, find_path(a, b, c)
        if a == n - 1:
            break

    return f"{res}\n{a + 1} {b + 1} {c + 1}"

