    n = int(stdin.split()[0])
    friends = list(map(int, stdin.split()[1:]))

    def dfs(idx, color, visited):
        if idx == n:
            return 1

        if friends[idx] == 0 or color[friends[idx] - 1] == 1:
            return 0

        color[idx] = 1
        num = dfs(friends[idx] - 1, color, visited)
        if num:
            visited.append(idx)
        color[idx] = 0
        return num

    visited = []
    color = [0] * n
    for i in range(n):
        if friends[i] == 0:
            continue
        if dfs(i, color, visited)!= 1:
            return ""

    result = []
    for i in range(n):
        if i not in visited:
            result.append(i + 1)

    result.sort()
    return " ".join(map(str, result))


