    def remove_segment(i: int) -> int:
        nonlocal segments

        length = 0
        segments = sorted([segment for j, segment in enumerate(segments) if j!= i])

        for j, segment in enumerate(segments):
            if segment[1] - segment[0] > 0:
                length += segment[1] - segment[0] - 1
            else:
                length += 0

        return length

    return str(max(map(remove_segment, range(n - 1))))

