    def sum_of_set(set_: set) -> int:
        return sum(set_)

    def absolute_difference(set1: set, set2: set) -> int:
        return max(sum_of_set(set1) - sum_of_set(set2), 0)

    sets = [set() for _ in range(2)]

    for num in sequence:
        sets[num - 1].add(num)

    smallest_difference = float('inf')

    for i, set1 in enumerate(sets):
        for j, set2 in enumerate(sets):
            if i!= j:
                difference = absolute_difference(set1, set2)
                smallest_difference = min(smallest_difference, difference)

    return str(smallest_difference)

def solution(stdin: str) -> str:
    n, s = map(int, stdin.split())
    MOD = 10**9 + 7

    def count_substrings(substring: str) -> int:
        substring_length = len(substring)
        count = 0
        for i in range(len(s) - substring_length + 1):
            if s[i:i+substring_length] == substring:
                count += 1
        return count

    def is_regular_expression(expression: str) -> bool:
        stack = []
        for char in expression:
            if char == '(':
                stack.append(char)
            elif char == ')':
                if not stack:
                    return False
                stack.pop()
            elif char == '+':
                if not stack:
                    return False
                if stack[-1] == '+':
                    return False
        return not stack

    regular_bracket_sequences = []
    for i in range(n):
        regular_bracket_sequences.extend([''.join(sorted(pair)) for pair in itertools.product(['(', ')'], repeat=i+1)])

    result = 0
    for regular_bracket_sequence in regular_bracket_sequences:
        if regular_bracket_sequence in s:
            if is_regular_expression(regular_bracket_sequence):
                result += 1

    return str(result % MOD)

