    s = stdin.strip()
    n = len(s)

    # Count the occurrences of each character
    char_count = [0] * 26
    for c in s:
        char_count[ord(c) - ord('a')] += 1

    # Find the minimum number of colors
    min_colors = 0
    for i in range(26):
        if char_count[i] > 0:
            min_colors += 1

    # Find the maximum number of colors that can be used
    max_colors = 0
    for i in range(n):
        for j in range(i + 1, n):
            if s[i]!= s[j]:
                max_colors += 1
                break

    # Find the minimum number of swaps required
    min_swaps = 0
    for i in range(n):
        for j in range(i + 1, n):
            if s[i] == s[j]:
                min_swaps += 1
                break

    # Find the maximum number of swaps that can be used
    max_swaps = 0
    for i in range(n):
        for j in range(i + 1, n):
            if s[i]!= s[j]:
                max_swaps += 1
                break

    # Find the answer
    res = min(min_colors, min_swaps)
    if res == min_colors:
        colors = [0] * n
        for i in range(n):
            colors[i] = 1
        for i in range(n):
            for j in range(i + 1, n):
                if s[i]!= s[j]:
                    colors[i], colors[j] = colors[j], colors[i]
        return res, ''.join([chr(ord('a') + c - 1) for c in colors])
    else:
        colors = [0] * n
        for i in range(n):
            colors[i] = 1
        for i in range(n):
            for j in range(i + 1, n):
                if s[i]!= s[j]:
                    colors[i], colors[j] = colors[j], colors[i]
        colors.sort()
        return res, ''.join([chr(ord('a') + c - 1) for c in colors])

