        roads[a - 1].append(b - 1)
        roads[b - 1].append(a - 1)

    def bfs(city: int) -> list:
        queue = [(city, [])]
        visited = set()
        while queue:
            city, path = queue.pop(0)
            if city not in visited:
                visited.add(city)
                path.append(city)
                if city!= n - 1:
                    queue.append((city + 1, path))
        return path

    def dfs(city: int) -> list:
        queue = [(city, [])]
        visited = set()
        while queue:
            city, path = queue.pop(0)
            if city not in visited:
                visited.add(city)
                path.append(city)
                if city!= n - 1:
                    queue.append((city + 1, path))
                    queue.append((city - 1, path))
                    queue.append((city + 2, path))
                    queue.append((city - 2, path))
        return path

    def min_distance(path: list) -> int:
        return sum(roads[city] for city in path[1:])

    def is_valid(roads: list) -> bool:
        visited = set()
        for road in roads:
            for city in road:
                if city not in visited:
                    visited.add(city)
                    if city == n - 1:
                        return False
        return True

