        city_i = 1
        if i == 0:
            city_i = 0

        for j in range(min(n - 1, k)):
            city_j = city_i + j
            if city_j == n - 1:
                city_j = 0

            path_i = dfs(city_i)
            path_j = dfs(city_j)
            if is_valid(path_i) and is_valid(path_j):
                path = path_i + path_j
                if len(path) == n - 1:
