                pointer += tape[pointer]
                if pointer < 0 or pointer >= len(memory):
                    raise ValueError('Invalid pointer')
            elif c in {'<', '>', '+', '-'}:
                if c == '+':
                    memory[pointer] += tape[pointer + 1]
                elif c == '-':
                    memory[pointer] -= tape[pointer + 1]
                pointer += tape[pointer + 1]
                if pointer < 0 or pointer >= len(memory):
                    raise ValueError('Invalid pointer')
            else:
                raise ValueError('Invalid character')
        return memory[0]

    operations = {'+': {'+': '+', '-': '-'}, '-': {'+': '-', '-': '+'}}

