    h = [0] * n + h

    def increase_height(l: int, r: int) -> None:
        for x in range(l, r + 1):
            h[x] += 1

    def watering_operations(start_index: int, target_index: int) -> int:
        operations = 0
        for i in range(start_index, target_index):
            if h[i] == h[i + 1]:
                return 0
            if h[i] < h[i + 1]:
                increase_height(i + 1, target_index)
                operations += 1
        return operations

    min_operations = float('inf')
    for i in range(n - 1):
        if h[i + 1] - h[i] == 1:
            min_operations = min(min_operations, watering_operations(i, i + 1))
        elif h[i + 1] - h[i] > 1:
            min_operations = min(min_operations, watering_operations(i, i + 1))
            min_operations = min(min_operations, watering_operations(i + 1, n - 1))

    return str(min_operations)


