    N, M, X, Y = map(int, stdin.split())
    x = list(map(int, stdin.split()))
    y = list(map(int, stdin.split()))

    def dfs(x: int, y: int, visited: set) -> int:
        if x == X and y == Y:
            return 0
        if (x, y) in visited:
            return 1
        visited.add((x, y))

        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            new_x = x + dx
            new_y = y + dy
            if 0 <= new_x < N and 0 <= new_y < M and (x, y)!= (new_x, new_y):
                if dfs(new_x, new_y, visited) == 1:
                    return 1

        return 0

    return "War" if dfs(0, 0, set()) == 1 else "No War"


