                if i < j:
                    blocks.add((i, j))
        return blocks

    def check_compatibility(block1: tuple, block2: tuple) -> bool:
        return block1[0] < block2[1] or block2[0] < block1[1]

    def find_max_k(start: int, end: int) -> int:
        max_k = 0
        for i in range(start, end + 1):
            blocks = find_blocks(i, end)
            for block1 in blocks:
                for block2 in blocks:
                    if block1!= block2 and check_compatibility(block1, block2):
                        max_k = max(max_k, block1[1] - block1[0] + 1)
        return max_k

    max_k = find_max_k(1, n)
    result = []
    for i in range(max_k):
        max_blocks = set()
        for j in range(max_k):
            blocks = find_blocks(i + 1, j + i + 1)
            for block in blocks:
                if block not in max_blocks and check_compatibility(block, (i + 1, j + i + 2)):
                    max_blocks.add(block)
        result.extend([f"{x[0]} {x[1]}" for x in sorted(list(max_blocks))])
        max_blocks.clear()

    return "\n".join(result)

