    if K == 1:
        return "OK"

    # find the lowest multiple of K that satisfies the constraints
    low_multiple = K * (A // K)
    if low_multiple < A:
        low_multiple += K

    # find the highest multiple of K that satisfies the constraints
    high_multiple = K * (B // K)
    if high_multiple > B:
        high_multiple -= K

    # calculate the gcd of the difference between the lowest and highest multiples
    gcd_diff = gcd(abs(low_multiple - high_multiple), K)

    # check if the gcd of the difference is equal to K
    if gcd_diff == K:
        return "OK"

    # if the gcd of the difference is not equal to K, the constraints do not match, so we cannot achieve the objective
    return "NG"

