def solution(stdin: str) -> str:
    n = int(stdin.split()[0])
    participants = [int(x) for x in stdin.split()[1:]]

    # sort the participants by weight
    participants.sort()

    def find_teams(i: int) -> int:
        # if i is the last participant, there is only one team
        if i == n:
            return 1

        # if i is the second last participant, there are only two teams
        if i == n - 1:
            return 2

        # if i is the second to last participant, there are only three teams
        if i == n - 2:
            return 3

        # if the current participant has the same weight as the previous participants, there is no need to add it to the team
        if i > 0 and participants[i] == participants[i-1]:
            return find_teams(i + 1)

        # if the current participant has a different weight from the previous participants, we need to add it to the team
        return find_teams(i + 1) + find_teams(i + 2)

    s = sum(participants)
    max_teams = 0

    for i in range(1, n):
        max_teams = max(max_teams, find_teams(i))

    return str(max_teams)

