        for letter in s:
            freq[letter] = freq.get(letter, 0) + 1
        return freq

    def find_best_string(n: int, k: int) -> str:
        letters = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j', 'k', 'l','m', 'n', 'o', 'p', 'q', 'r','s', 't', 'u', 'v', 'w', 'x', 'y', 'z']
        result = []

        def is_valid(s: str) -> bool:
            freq = count_freq(s)
            for i in range(1, min(len(s), k) + 1):
                if s[i] not in freq or freq[s[i]] < i:
                    return False
            return True

        def backtrack(s: str = '', freq: dict = None, valid_so_far: int = 0) -> None:
            if len(s) == n:
                if valid_so_far > best_freq:
                    best_freq = valid_so_far
                    best_string = s
                return

            for i in range(1, min(len(s), k) + 1):
                if s in used_strings:
                    continue
                used_strings.add(s)
                if i <= len(s) and s[i] == s[i - 1]:
                    continue
                if s[i] in freq:
                    backtrack(s + s[i], freq, valid_so_far + freq[s[i]])
                else:
                    backtrack(s + letters[i - 1], freq, valid_so_far)

            used_strings.pop()

        used_strings = set()
        best_freq = 0
        best_string = ''

        for _ in range(k):
            backtrack()

        return best_string

