def solution(stdin: str) -> str:
    grid = []
    for line in stdin.split('\n'):
        row = list(map(int, line.split()))
        if len(row)!= 16:
            return 'Invalid input'
        grid.append(row)

    move = int(stdin.split('\n')[4])

    if move not in [0, 1, 2, 3]:
        return 'Invalid input'

    def move_tile(row, col, direction):
        nonlocal grid

        if direction == 0:
            row -= 1
        elif direction == 1:
            col -= 1
        elif direction == 2:
            row += 1
        else:
            col += 1

        if not (0 <= row < 4 and 0 <= col < 4):
            return

        if grid[row][col] == 0:
            return

        if grid[row][col] == grid[row][col - 1]:
            grid[row][col - 1] *= 2
            grid[row][col] = 0
        elif grid[row][col] == grid[row - 1][col]:
            grid[row - 1][col] *= 2
            grid[row][col] = 0
        elif grid[row][col] == grid[row - 1][col - 1]:
            grid[row - 1][col - 1] *= 2
            grid[row][col] = 0
        elif grid[row][col] == grid[row - 1][col + 1]:
            grid[row - 1][col + 1] *= 2
            grid[row][col] = 0
        elif grid[row][col] == grid[row + 1][col - 1]:
            grid[row + 1][col - 1] *= 2
            grid[row][col] = 0
        elif grid[row][col] == grid[row + 1][col + 1]:
            grid[row + 1][col + 1] *= 2
            grid[row][col] = 0
        elif grid[row][col] == grid[row + 1][col]:
            grid[row + 1][col] *= 2
            grid[row][col] = 0
        elif grid[row][col] == grid[row][col - 1]:
            grid[row][col - 1] *= 2
            grid[row][col] = 0
        elif grid[row][col] == grid[row][col + 1]:
            grid[row][col + 1] *= 2
            grid[row][col] = 0
        else:
            return

    def merge_tiles(row, col):
        nonlocal grid

        for i in range(1, 4):
            for j in range(1, 4):
                if grid[row + i][col + j] == grid[row][col]:
                    grid[row + i][col + j] *= 2
                    grid[row][col] = 0

    row = 0
    col = 0

    if move == 0:
        pass
    elif move == 1:
        move_tile(row, col, 0)
    elif move == 2:
        move_tile(row, col, 2)
    else:
        move_tile(row, col, 1)

    merge_tiles(row, col)

    for row in grid:
        row.append(0)

    return '\n'.join([' '.join(str(x) for x in row) for row in grid])

