def solution(stdin: str) -> str:
    # Convert all words to lowercase
    words = [word.lower() for word in stdin.split()]

    # Define a mapping of consonants to their nearest equivalent in nimionese
    consonants = {'b': 'd', 'c': 'd', 'd': 'd', 'g': 'g', 'k': 'g', 'n': 'gn', 'p': 'g', 't': 't'}

    # Iterate over each word and translate it to nimionese
    for i, word in enumerate(words):
        # Convert the first letter of each word to nimionese
        words[i] = consonants.get(word[0], word[0]) + word[1:]

        # Replace subsequent syllables with the same consonant as the start of the word
        for j in range(len(word) - 1):
            if consonants.get(word[j], word[j])!= word[j]:
                words[i] = words[i][:j] + consonants.get(word[j], word[j]) + words[i][j + 1:]

        # Add an 'ah', 'oh' or 'uh' at the end, rounding toward 'A' in the case of a tie
        if word[-1] in consonants:
            words[i] += 'ah'
        elif word[-1] in 'aeiou':
            words[i] += 'oh'
        else:
            words[i] += 'uh'

    # Join the words back together and remove any dashes
    return '-'.join(words)

