    if pos == -1:
        return 'false'

    def detectCycle(head: ListNode) -> bool:
        slow, fast = head, head
        while fast and fast.next:
            slow = slow.next
            fast = fast.next.next
            if slow == fast:
                return True
        return False

    def hasCycle(head: ListNode, pos: int) -> bool:
        slow, fast = head, head
        while fast and fast.next:
            slow = slow.next
            if pos >= 0:
                pos -= 1
            if pos == 0:
                return detectCycle(slow)
            fast = fast.next.next
        return detectCycle(slow)

    return 'true' if hasCycle(head, pos) else 'false'

