        for i in range(len(types)):
            for j in range(i + 1, len(types)):
                for k in range(j + 1, len(types)):
                    if all(types[t] == types[u] and types[t] == types[v] for t, u, v in combinations(piles, 3)):
                        if (frozenset(piles), frozenset(types)) not in seen:
                            seen.add((frozenset(piles), frozenset(types)))
                            return 0, 0

        def dfs(piles: List[int], types: List[int], depth: int) -> int:
            nonlocal min_moves
            nonlocal result

            if depth == len(types) - 1:
                min_moves = min(min_moves, depth)
                result = depth
                return 0

            for i in range(len(piles)):
                if i in used:
                    continue

                used.add(i)
                for j in range(i + 1, len(piles)):
                    if j in used:
                        continue

                    used.add(j)
                    for k in range(j + 1, len(piles)):
