        return sum(1 for c1, c2 in zip(s1, s2) if c1!= c2)

    def merge_sort(arr: list) -> list:
        if len(arr) <= 1:
            return arr
        mid = len(arr) // 2
        left = merge_sort(arr[:mid])
        right = merge_sort(arr[mid:])
        return merge(left, right)

    def merge(left: list, right: list) -> list:
        result = []
        i, j = 0, 0
        while i < len(left) and j < len(right):
            if hamming_distance(left[i], right[j]) < hamming_distance(left[i], result) + hamming_distance(result, right[j]):
                result.append(left[i])
                i += 1
            else:
                result.append(right[j])
                j += 1
        result.extend(left[i:])
        result.extend(right[j:])
        return result

    def compute_unlikelihood(tree: list) -> int:
        return sum(hamming_distance(tree[u], tree[v]) * (u - v) for u, v in combinations(range(len(tree)), 2))

    tree = merge_sort(["".join(sample) for sample in samples])
    unlikelihood = compute_unlikelihood(tree)

