        parking_spaces.append([c == 'X' for c in row_str])

    monster_truck = [
        [1, 1],
        [1, 1]
    ]

    possible_parks = []

    def backtrack(row: int, col: int, squashed_cars: int) -> None:
        if squashed_cars > 4:
            return

        if row == len(parking_spaces) or col == len(parking_spaces[row]) or parking_spaces[row][col]:
            return

        parking_spaces[row][col] = True

        if not monster_truck or (row + monster_truck[0][0] - 1 < len(parking_spaces) and col + monster_truck[0][1] - 1 < len(parking_spaces[0]) and parking_spaces[row + monster_truck[0][0] - 1][col + monster_truck[0][1] - 1]):
            possible_parks.append(squashed_cars)

        backtrack(row + 1, col, squashed_cars)
        backtrack(row - 1, col, squashed_cars)
        backtrack(row, col + 1, squashed_cars)
        backtrack(row, col - 1, squashed_cars)

        parking_spaces[row][col] = False

    backtrack(0, 0, 0)

    return '\n'.join(map(str, possible_parks))

