    def closest_pair(points: list[int]) -> tuple[int, int]:
        n = len(points)
        min_distance = float('inf')
        closest_pair = (0, 0)

        for i in range(n):
            for j in range(i + 1, n):
                distance = hamming_distance(points[i], points[j])
                if distance < min_distance:
                    min_distance = distance
                    closest_pair = (i, j)

        return min_distance, closest_pair

    def arora_mitchell(points: list[int], c: float) -> float:
        n = len(points)
        min_distance, closest_pair = closest_pair(points)
        max_distance = min_distance * (1 + 1 / c)

        while max_distance > min_distance:
            mid = (max_distance + min_distance) / 2
            distance, pair = closest_pair(points)

            if distance < mid:
                min_distance = mid
            else:
                max_distance = mid

        return max_distance

    def process_pair(points: list[int], c: float) -> float:
        min_distance = arora_mitchell(points, c)
        return min_distance * (1 + 1 / c)

    keys = [random.randint(1, 100) for _ in range(n)]
