        queue = [start]
        visited.add(start)
        distances[start] = 0

        while queue:
            current = queue.pop(0)
            for neighbor in range(1, n * n + 1):
                if neighbor == current:
                    continue
                if neighbor not in visited and tiles[current - 1][neighbor - 1] == 1 and distances[neighbor] > distances[current] + 1:
                    distances[neighbor] = distances[current] + 1
                    queue.append(neighbor)
                    visited.add(neighbor)

        return distances[n * n]

    return str(bfs(1))

