    def is_satisfiable(clause, assignments):
        def evaluate_literal(literal, assignment):
            if literal == -literal:
                return assignment == literal
            else:
                return assignment == literal - 1

        for literal in clause:
            if not evaluate_literal(literal, assignments[clause.index(literal)]):
                return False
        return True

    satisfiable = all(is_satisfiable(clause, {i: False for i in range(n + 1)}) for clause in clauses)

    if satisfiable:
        return "satisfactory"
    else:
        return "unsatisfactory"

