    knight_position = [(N, N)]
    for i in range(N):
        for j in range(N):
            if board[i][j] == 'K':
                knight_position = [(i, j)]
                break

    def is_valid(r, c):
        return 1 <= r <= N and 1 <= c <= N

    def is_blocked(r, c):
        return board[r - 1][c - 1] == '#' or board[r - 1][c] == '#' or board[r - 1][c + 1] == '#' or \
               board[r][c - 1] == '#' or board[r][c + 1] == '#' or board[r + 1][c - 1] == '#' or \
               board[r + 1][c] == '#' or board[r + 1][c + 1] == '#'

    def knight_moves(r, c):
        return [(r + 2, c + 1), (r + 2, c - 1), (r - 2, c + 1), (r - 2, c - 1), (r + 1, c + 2),
                (r + 1, c - 2), (r - 1, c + 2), (r - 1, c - 2)]

    # bfs
    visited = set()
    queue = [knight_position]
    steps = 0
    while queue:
        current_position = queue.pop(0)
        steps += 1
        if current_position == (1, 1):
            return str(steps)
        if tuple(current_position) in visited:
            continue
        visited.add(tuple(current_position))

        for move in knight_moves(current_position[0], current_position[1]):
            if is_valid(move[0], move[1]) and not is_blocked(move[0], move[1]):
                queue.append((move[0], move[1]))

    return "-1"

