    n = int(stdin.split()[0])
    f = [int(i) for i in stdin.split()[1:]]

    # make sure there is at least two values that are not 0
    if f.count(0) < 2:
        return " ".join(map(str, [0] * n))

    # set values of unknown friends to 0
    for i, j in enumerate(f):
        if j == 0:
            f[i] = 0

    # count the number of gifts each friend receives
    receives = [0] * n
    for i in range(n):
        if f[i] > 0:
            receives[f[i] - 1] += 1

    # find the friend with the maximum number of gifts that he receives
    max_index = -1
    max_receives = 0
    for i, r in enumerate(receives):
        if r > max_receives:
            max_index = i
            max_receives = r

    # set the value of the friend with the maximum number of gifts that he receives to 0
    f[max_index] = 0

    # count the number of gifts each friend receives
    receives = [0] * n
    for i in range(n):
        if f[i] > 0:
            receives[f[i] - 1] += 1

    # find the friend with the minimum number of gifts that he receives
    min_index = -1
    min_receives = n
    for i, r in enumerate(receives):
        if r < min_receives:
            min_index = i
            min_receives = r

    # set the value of the friend with the minimum number of gifts that he receives to 0
    f[min_index] = 0

    # print the values of the friends
    return " ".join(map(str, f))


