def solution(stdin: str) -> str:
    n, x, y = map(int, stdin.strip().split())
    num = [int(digit) for digit in stdin[1:]]
    num.reverse()

    def count_zeros(num: List[int]) -> int:
        count = 0
        for digit in num:
            if digit == 0:
                count += 1
        return count

    def count_ones(num: List[int]) -> int:
        count = 0
        for digit in num:
            if digit == 1:
                count += 1
        return count

    def reverse_zeros_ones(num: List[int]) -> List[int]:
        zeros = []
        ones = []
        for digit in num:
            if digit == 0:
                zeros.append(0)
            else:
                ones.append(1)
        return zeros + ones

    def count_ones_after_operation(num: List[int], operation: Callable[[List[int]], List[int]]) -> int:
        num = operation(num)
        return count_ones(num)

    def modulo(a: int, b: int) -> int:
        return a - b * (a // b)

    def calculate_remainder(num: List[int]) -> int:
        zeros = count_zeros(num)
        ones = count_ones(num)
        ones_after_operations = count_ones_after_operation(num, reverse_zeros_ones)

        return modulo(10 ** y, modulo(10 ** zeros * 10 ** ones, 10 ** x * 10 ** ones_after_operations))

    min_operations = calculate_remainder(num)

    return str(min_operations)

