    def gcd(a: int, b: int) -> int:
        while b:
            a, b = b, a % b
        return a

    def lcm(a: int, b: int) -> int:
        return a * b // gcd(a, b)

    def count_divisors(n: int) -> int:
        count = 0
        for i in range(1, int(n ** 0.5) + 1):
            if n % i == 0:
                count += 2
        if n > 1:
            count += 1
        return count

