            return "IMPOSSIBLE"

        lcm_ab = lcm(a, b)
        lcm_bc = lcm(b, c)
        lcm_ca = lcm(c, a)

        if gcd(lcm_ab, lcm_bc) == 1:
            continue

        count_ab = count_divisors(lcm_ab)
        count_bc = count_divisors(lcm_bc)
        count_ca = count_divisors(lcm_ca)

