    n, k = map(int, stdin.split())
    s = stdin.strip()

    def backtrack(start, length, current_set, cost):
        if len(current_set) == k:
            return cost

        if len(current_set) > k:
            return float('inf')

        for i in range(start, n):
            if i not in current_set and s[i] not in current_set:
                current_set.add(s[i])
                cost += n - 1
                result = backtrack(i + 1, length, current_set, cost)
                current_set.remove(s[i])
                if result!= float('inf'):
                    return result

        return float('inf')

    result = backtrack(0, n, set(), 0)
    return str(result if result!= float('inf') else -1)

