    n = int(stdin.split()[0])
    s = stdin.split()[1]

    MOD = 1000000007

    def is_valid(seq: str) -> bool:
        if len(seq) % 2 == 1:
            return False

        stack = []
        for c in seq:
            if c == '(':
                stack.append(c)
            elif c == ')':
                if not stack:
                    return False
                stack.pop()
            else:
                if not stack or stack[-1] == '(':
                    stack.append(c)
                else:
                    return False
        return not stack

    def count_regular(seq: str) -> int:
        if not is_valid(seq):
            return 0

        n = len(seq)
        result = [0] * (n + 1)
        result[0] = 1
        result[1] = 1

        for i in range(2, n + 1):
            for j in range(i):
                if seq[j:i] == s:
                    result[i] += result[j]
                    result[i] %= MOD

        return result[n]

    return str(count_regular(s)) % MOD

