    # Build a graph of connected switches
    graph = [[] for _ in range(n + 1)]
    for i in range(m):
        u, v = map(int, stdin.split())
        graph[u].append(v)
        graph[v].append(u)

    # Count the number of connected components in the graph
    def count_connected_components(graph: list) -> int:
        visited = [False] * (n + 1)
        stack = [(0, 0)]
        count = 0
        while stack:
            u, v = stack.pop()
            if visited[u]:
                continue
            visited[u] = True
            stack.extend((w, v) for w in graph[u] if not visited[w])
            count += 1
        return count

    components = count_connected_components(graph)
    if components % 2 == 0:
        result = 1
    else:
        result = components

    # Count the number of combinations of on and off states of switches
    def count_combinations(graph: list) -> int:
        def backtrack(node: int, combinations: int, on: int, off: int) -> int:
            if node == n + 1:
                return 1 if combinations % 2 == 0 else 0
            if on > off:
                return backtrack(node + 1, combinations, on, off)
            return (
                backtrack(node + 1, combinations, on + 1, off)
                + backtrack(node + 1, combinations, on, off + 1)
            )

        return backtrack(1, 0, 0, 0)

    combinations = count_combinations(graph)
    result *= combinations

    return str(result)

