        for i in range(len(lst)):
            j = i + random.randint(0, len(lst) - 1 - i)
            lst[i], lst[j] = lst[j], lst[i]

    def shuffle_and_merge(a: list, b: list) -> list:
        shuffle(a)
        result = []
        for num in a + b:
            if is_prime(num):
                result.append(p[num - 2])
            else:
