    # Count the number of ways to reach the first bus stop
    dp = [0] * (w + 1)
    dp[0] = 1

    for i in range(1, w + 1):
        for j in range(n):
            # If we already reached the bus stop
            if i == a[j]:
                dp[i] += dp[i - a[j]]
            else:
                # Otherwise, we need to choose between the following options:
