        if b == 0:
            return a
        return gcd(b, a % b)

    def lcm(a, b):
        return a * b // gcd(a, b)

    def is_power_of_two(n):
        return gcd(n, 2) == 1

    def get_subset(arr):
        total = lcm(*arr)
        subset_size = total // 2
        return sorted(set(arr) & set(map(lambda x: x * subset_size, arr)))

    max_size = 1
    max_subset = []
    for i in range(1, n):
        if is_power_of_two(abs(x[i] - x[i - 1])):
            max_subset.append(x[i])
            max_size *= 2
        else:
            break

    for i in range(max_size):
        subset = get_subset(max_subset)
        if len(subset) == max_size:
            break
    else:
        return " ".join(map(str, max_subset))

    return " ".join(map(str, subset))

