    grid = list(map(str, stdin.splitlines()[1:]))

    def is_valid(row: int, col: int, size: int) -> bool:
        for i in range(size):
            if row - i < 0 or row + i >= n or col - i < 0 or col + i >= m or grid[row - i][col - i]!= '*':
                return False
        return True

    def count_stars(row: int, col: int, size: int) -> int:
        count = 1
        for i in range(size):
            new_row = row - i
            new_col = col - i
            if new_row >= 0 and new_row < n and new_col >= 0 and new_col < m:
                if grid[new_row][new_col] == '*':
                    count += count_stars(new_row, new_col, size)
        return count

    def get_star(row: int, col: int, size: int) -> tuple[int, int, int]:
        while not is_valid(row, col, size):
            size -= 1
        return row, col, size

    stars = []
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '*':
                row, col, size = get_star(i, j, 1)
                stars.append((row, col, size))

    k = 0
    for i in range(len(stars)):
        for j in range(i + 1, len(stars)):
            if stars[i][2] == stars[j][2] and (stars[i][0] > stars[j][0] or (stars[i][0] == stars[j][0] and stars[i][1] < stars[j][1])):
                k -= min(stars[i][2], stars[j][2])
                stars.remove(stars[j])
                j -= 1

    if k > n * m:
        return "-1"

    return str(k) + '\n' + '\n'.join(f"{row} {col} {size}" for row, col, size in stars)


