        total_weight = 0
        for i in range(len(segments)):
            if segments[i][0] <= x and x < segments[i][1]:
                total_weight += segments[i][1] - segments[i][0]
        return total_weight

    def umbrella_strategy(a: int, segments: List[Tuple[int, int]], umbrellas: List[int], x: int, max_weight: int) -> Tuple[int, int]:
        max_weight_umbrella = 0
        max_weight_umbrella_segment = None
        for i in range(len(segments)):
            if segments[i][0] <= x and x < segments[i][1]:
                weight = umbrellas[i]
                if weight > max_weight:
                    max_weight = weight
                    max_weight_umbrella = weight
                    max_weight_umbrella_segment = i
        if max_weight_umbrella == 0:
            return (max_weight, max_weight_umbrella_segment)
        if max_weight_umbrella > max_weight:
            max_weight = max_weight_umbrella
            max_weight_umbrella_segment = i
        return (max_weight, max_weight_umbrella_segment)

