            return 0
        return dp[t]

    def dfs(s: int, t: int) -> int:
        if s == t:
            return 1
        if s < t:
            return 0

        result = 0
        for i in range(s, t + 1):
            result += dfs(s, i - 1) * dfs(i + 1, t)

        return result

