        vis[u] = True
        for v in adj[u]:
            if not vis[v]:
                if has_cycle(v):
                    return True
        vis[u] = False
        return False

    def kruskal():
        edges = []
        for u, v, w in edges:
            if not is_connected(u, v):
                edges.append((u, v, w))
        return edges

    def get_mst():
        edges = kruskal()
        min_weight = float('inf')
        for u, v, w in edges:
            min_weight = min(min_weight, w)
        return min_weight

    def increase_weight(u, v, w):
