def solution(stdin: str) -> str:
    def dijkstra(graph, start, visited):
        dist = [float('inf')] * len(graph)
        dist[start] = 0
        prev = [None] * len(graph)

        for _ in range(len(graph)):
            min_idx = start
            for i, d in enumerate(dist):
                if d < dist[min_idx] and not visited[i]:
                    min_idx = i

            if min_idx == start:
                break

            visited[min_idx] = True
            for neighbor, weight in graph[min_idx]:
                if not visited[neighbor]:
                    new_dist = dist[min_idx] + weight
                    if new_dist < dist[neighbor]:
                        dist[neighbor] = new_dist
                        prev[neighbor] = min_idx

        return dist, prev

    def kth_smallest_shortest_path(dist, prev, k):
        result = []
        current = len(dist) - 1

        while current!= -1:
            result.append(dist[current])
            current = prev[current]

        result.reverse()
        return result[:k]

    n, m, k = map(int, stdin.split())
    graph = [[] for _ in range(n)]

    for _ in range(m):
        x, y, w = map(int, stdin.split())
        x -= 1
        y -= 1
        graph[x].append((y, w))
        graph[y].append((x, w))

    start = 0
    visited = [False] * n

    dist, prev = dijkstra(graph, start, visited)
    kth_smallest_shortest_path_length = kth_smallest_shortest_path(dist, prev, k)[-1]

    return str(kth_smallest_shortest_path_length)

