    def merge_strings(s: str, t: str) -> str:
        i = 0
        while i < len(s) and i < len(t):
            if s[i] < t[i]:
                s = s[:i] + t[i] + s[i:]
                t = t[:i] + s[i:]
            else:
                s = s[:i+1] + s[i+1:]
                t = t[:i+1] + t[i+1:]
            i += 1
        return s + t

    def earn(s: str, t: str, cards: list) -> int:
        count = 0
        for card in cards:
            if check(card[1], s):
                count += 1
        return count * 10

    def lose(s: str, t: str, cards: list) -> int:
        count = 0
        for card in cards:
            if check(card[1], s):
                count += 1
        return count * -1

    max_earn = 0
    for s_i in s:
        for t_i in t:
            cards = [(i, t_i) for i in range(n)]
            cards.extend([(i, s_i) for i in range(m, n)])
            earn_count = earn(s_i, t_i, cards)
