        adj[u - 1].append(v - 1)
        adj[v - 1].append(u - 1)

    def bfs(start: int, visited: list[bool]) -> int:
        queue = [(start, 0)]
        visited[start] = True

        while queue:
            vertex, distance = queue.pop(0)

            if distance > a[vertex]:
                continue

            for neighbor in adj[vertex]:
                if not visited[neighbor]:
                    queue.append((neighbor, distance + 1))
                    visited[neighbor] = True

        return distance

    visited = [False] * n
    max_cost = float('-inf')

    for i in range(n):
        if not visited[i]:
            distance = bfs(i, visited)
            if distance > max_cost:
                max_cost = distance

    return str(max_cost)

